const buyButtons = document.querySelectorAll(".search-results-goods .buy-link-subject"), binForm = document.querySelector(".modal-bin"), binFormCloseButon = document.querySelector(".modal-bin-close"); buyButtons.forEach(e => { e.addEventListener("click", function (e) { e.preventDefault(), binForm.classList.contains("visually-hidden") && (binForm.classList.remove("visually-hidden"), binFormCloseButon.addEventListener("click", closeModal)) }) }); let closeModal = function (e) { e.preventDefault(), binForm.classList.add("visually-hidden"), binFormCloseButon.removeEventListener("click", closeModal) };
